                  signed int box_height = boxpos[1] - boxorg[1];
                  signed int box_center[2] = {
                      boxorg[0] + (box_width / 2),
                      boxorg[1] + (box_height / 2)
                      };

                  if (box_width < 0) {box_width = -box_width;}
                  if (box_height < 0) {box_height = -box_height;}

